INSERT INTO `t_knowledge` VALUES (171, '机箱的扩展性', '机箱的扩展性包括支持的硬盘位、光驱位和扩展槽。更多的扩展选项可以方便用户安装额外的硬件组件。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (172, '机箱的材料', '机箱的材料包括钢铁、铝合金和塑料等。不同的材料在耐用性、散热性能和重量上有所不同。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (173, '机箱的设计', '机箱的设计包括外观造型、内部布局和功能性设计。一个好的设计可以提高机箱的美观性和使用方便性。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (174, '机箱的未来发展', '未来的机箱发展可能包括更好的散热技术、更智能的管理功能和更灵活的设计选项。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (175, '机箱的历史', '机箱的历史可以追溯到计算机发展的早期阶段。从最初的金属外壳到现代的多功能机箱，机箱技术不断进步。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (176, '机箱的电源设计', '机箱的电源设计包括电源位置、线缆管理和通风设计。良好的电源设计可以提高系统的稳定性和散热效果。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (177, '机箱的噪音控制', '机箱的噪音控制包括使用隔音材料和风扇调速。有效的噪音控制可以提供更安静的计算环境。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (178, '机箱的灯光设计', '机箱的灯光设计包括 LED 照明和光效控制。灯光设计不仅提升美观，还可以增强系统的个性化。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (179, '机箱的过滤系统', '机箱的过滤系统包括前面板和底部的灰尘过滤网。过滤系统可以减少灰尘进入机箱内部，保持内部组件的清洁。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (180, '机箱的安装便利性', '机箱的安装便利性包括工具无工具安装、拆装简便性和内部空间布局。便利的安装设计可以提高用户的使用体验。', 8, '机箱', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (181, '什么是散热器', '散热器是用于散发计算机内部产生的热量的设备，通常安装在 CPU、GPU 或其他发热组件上。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (182, '散热器的作用', '散热器的主要作用是将组件产生的热量散发到空气中，从而防止过热和保证系统的稳定运行。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (183, '散热器的类型', '散热器的类型包括风冷散热器和水冷散热器。风冷散热器使用风扇将热量带走，而水冷散热器通过液体冷却系统进行散热。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (184, '散热器的工作原理', '散热器的工作原理是将热量从发热组件传导到散热片，然后通过风扇或液体冷却系统将热量带走。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (185, '如何选择散热器', '选择散热器时需考虑兼容性、散热性能、噪音水平和价格。确保选择的散热器适合你的系统需求。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (186, '散热器的品牌', '市场上的散热器品牌包括 Noctua、Corsair、Cooler Master 和 Be Quiet。每个品牌在性能、噪音和价格上有所不同。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (187, '散热器的价格', '散热器的价格因品牌、类型和性能而异。高性能的散热器通常价格较高，但提供更好的散热效果。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (188, '散热器的安装', '散热器的安装通常包括将散热器固定在 CPU 或 GPU 上，并连接风扇或液体冷却系统。安装时需注意兼容性和安装步骤。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (189, '散热器的风扇', '散热器的风扇包括静音风扇和高风量风扇。选择风扇时需考虑噪音水平和风量，以满足散热需求。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (190, '散热器的材质', '散热器的材质包括铝合金、铜和镁合金。不同的材质在导热性和重量上有所不同，选择时需考虑散热效果和系统负荷。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (191, '散热器的兼容性', '散热器的兼容性包括与主板、CPU 和机箱的适配。确保散热器能够安装在系统中，并与其他组件兼容。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (192, '散热器的散热效率', '散热器的散热效率包括热导管数量、散热片面积和风扇性能。良好的散热效率有助于降低系统温度。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (193, '散热器的噪音控制', '散热器的噪音控制包括使用低噪音风扇和优化风流设计。有效的噪音控制可以提供更安静的计算环境。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (194, '散热器的保养', '散热器的保养包括定期清洁散热片和风扇。保持散热器的清洁可以提高散热性能和系统稳定性。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (195, '散热器的未来发展', '未来的散热器发展可能包括更高效的散热技术、更低的噪音水平和更智能的控制系统。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (196, '什么是水冷散热', '水冷散热是指使用液体冷却系统来散热，通常包括水泵、冷排和液体管路。水冷散热提供更好的散热效果和更低的噪音。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:27', 'bbbmmm', '2024-09-04 04:29:27');
INSERT INTO `t_knowledge` VALUES (197, '什么是风冷散热', '风冷散热是指使用风扇和散热片来散热，通常较为简单且成本较低。风冷散热适合大多数普通用户和中低性能系统。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:28', 'bbbmmm', '2024-09-04 04:29:28');
INSERT INTO `t_knowledge` VALUES (198, '散热器的历史', '散热器的历史可以追溯到早期计算机系统的散热需求。从最初的简单风扇到现代的高效水冷系统，散热器技术不断进步。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:28', 'bbbmmm', '2024-09-04 04:29:28');
INSERT INTO `t_knowledge` VALUES (199, '散热器的应用场景', '散热器的应用场景包括高性能计算机、游戏主机和服务器等。不同的应用场景对散热器的要求有所不同。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:28', 'bbbmmm', '2024-09-04 04:29:28');
INSERT INTO `t_knowledge` VALUES (200, '散热器的工作环境', '散热器的工作环境包括计算机内部的温度、空气流动和灰尘量。良好的工作环境有助于提高散热器的性能和寿命。', 3, '散热器', 'bbbmmm', '2024-09-04 04:29:28', 'bbbmmm', '2024-09-04 04:29:28');
INSERT INTO `t_knowledge` VALUES (201, '低时序的内存对游戏的帧数影响', '对于网游游戏，内存的时序越低，帧数会越高，时序是影响内存处理速度的关键因素，时序越低，处理速度越快。对于网游游戏。会有直观的感受', 6, '内存', 'bbbmmm', '2024-09-06 01:21:50', 'bbbmmm', '2024-09-06 01:21:50');
